import './App.css';
import {useState} from "react"

function App() {
  let [salary, setSalary] = useState(0)

  const handleChange = (e) => {
    setSalary(e.target.value)
  }
  console.log(salary)
  return (
    <div className="App">
      <div>
        <label>{salary}</label>
        <input type= "text" value={salary} onChange={(e) => handleChange(e)}/>
      </div>
      <Clild salary={salary} onChange = {handleChange}/>
    </div>
  );
}


function Clild ({salary, onChange}){

  const handleClick = (e) => {
    onChange(e)
  }
  return(
    <>
      <label>child salary</label>
      <input type="text" value={salary} onChange={handleClick}/>
    </>
  )
}

export default App;
