import './App.css';
import {useState} from "react"

function App() {
  let [salary, setSalary] = useState(0)

  const handleChange = (e) => {
    setSalary(e.target.value)
  }
  console.log(salary)
  return (
    <div className="App">
      <div>
        <label>parent salary</label>
        <input type= "text" value={salary} onChange={handleChange}/>
      </div>
      <Clild salary={salary}/>
    </div>
  );
}


function Clild ({salary}){
  return(
    <>
      <label>child salary</label>
      <input type="text" value={salary}/>
    </>
  )
}

export default App;
