import './common.css'
const SiblingsData = () => {
    let [data, setData] = useState()
    return(
        <div className="siblingContainer">
                <LeftBar/>
                <RightBar onClick={setData}/>
        </div>
    )
}

export default SiblingsData


const LeftBar = ({onClick}) => {
    return(
        <div className='leftbarContainer'>
                <button className='leftbarButton' onClick={onClick}>open</button>
                <button className='leftbarButton' onClick={onClick}>close</button>
                <button className='leftbarButton' onClick={onClick}>small</button>
                <button className='leftbarButton' onClick={onClick}>large</button>
                <button className='leftbarButton' onClick={onClick}>rotate</button>
        </div>
    )
}

const RightBar = () => {
    return(
        <div className='rightbarContainer'>
                <div className='rightbarBox'>Box</div>
        </div>
    )
}
