/** HoC is the function that take component as an argument and return new component */

import { useState } from "react"


const ClickCounter = () => {
    let [count, setCount] = useState(0)
    return(
        <button onClick = {() => setCount(prevCount=> prevCount + 1)}>Clicked {count} times</button>
    )
}

export const HoverCounter = () => {
    let [count, setCount] = useState(0)
    return(
        <h2 onMouseHover = {() => setCount(prevCount=> prevCount + 1)}>Clicked {count} times</h2>
    )
}

export default ClickCounter
